package com.kuki.social_networking.mapper;import com.kuki.social_networking.model.Ingredient;import com.kuki.social_networking.response.IngredientDetailResponse;import com.kuki.social_networking.response.IngredientSummaryResponse;import org.mapstruct.Mapper;import org.mapstruct.Mapping;import org.mapstruct.Mappings;@Mapper(componentModel = "spring")public interface IngredientMapper {    @Mappings({        @Mapping(source = "id", target = "id"),        @Mapping(source = "name", target = "name"),        @Mapping(source = "photoUrl", target = "photoUrl"),    })    IngredientSummaryResponse toIngredientSummaryResponse(Ingredient ingredient);    @Mappings({        @Mapping(source = "id", target = "id"),        @Mapping(source = "name", target = "name"),        @Mapping(source = "photoUrl", target = "photoUrl"),        @Mapping(source = "nutritionalInfo", target = "nutritionalInfo"),        @Mapping(source = "unit", target = "unit"),    })    IngredientDetailResponse toIngredientDetailResponse(Ingredient ingredient);}